import React, { useState, useContext, useEffect } from "react";
import {
  CRow,
  CCol,
  CButton,
  CForm,
  CSelect,
  CLabel,
  CInput,
} from "@coreui/react";
import { AutoSizer, Table, Column } from "react-virtualized";
import Edit from "../../images/image 2.svg";
import Remove from "../../images/image 15.svg";
// import { useSnackbar } from 'notistack'
// import { useFormik } from 'formik'
// import * as yup from 'yup'
import { Card } from "@material-ui/core";
// import { GlobalContext } from '../../../services/GlobalContext'
import { AuthContext } from "../../../services/auth/AuthContext";
// import "../suspend/node_modules/react-virtualized/styles.css";
import { Checkbox } from "semantic-ui-react";
import Select from "react-select";
// import ImageUploading from "react-images-uploading";
import "react-virtualized/styles.css";
import "./Promote.css";
import {
  getAllDepartment,
  getAllRole,
  getAllDesignation,
  getOfficeLocation,
  getOfficeType,
  addPromoteEmployee,
  getFutureJob,
  updateEmployeeMovement,
} from "../../../services/ApiService";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
// const { elements } = this.props;
// const { filterStr } = this.state;

const AddPromoteEmployee = (props) => {
  const [checked, setChecked] = useState([]);
  const [allRoles, setAllRoles] = useState([]);
  const [listData, setListData] = useState([]);

  const [selected, setSelected] = useState({});

  const [updateDetails, setUpdateDetails] = useState("");

  const [roleList, setRoleList] = useState([]);
  const [roleOption, setRoleOption] = useState([]);

  const [relieving, setRelieving] = useState("");
  const [relievingError, setRelievingError] = useState("");

  const [joining, setJoining] = useState("");
  const [joiningError, setJoiningError] = useState("");

  const [office, setOffice] = useState("");

  const [description, setDescription] = useState("");

  const [final, setFinal] = useState([]);

  const [allRole, setAllRole] = useState([]);

  const [vacancy, setvacancy] = useState("");

  // const global = useContext(GlobalContext)

  const _noContentRenderer = () => {
    return <div>No Office types.</div>;
  };

  const onChangeRowCheckbox = (data) => {
    const newRow = final[data.index]._id;
    if (checked.includes(newRow)) {
      let data = checked.filter((id) => {
        return id !== newRow;
      });
      setChecked(data);
    } else {
      setChecked([newRow]);
    }
    console.log(checked);
  };

  useEffect(() => {
    const modifyData = async () => {
      const array = allRoles;
      let data = [];
      let existing = [];
      if (vacancy === "" || vacancy === "UnAllocatedRoles") {
        try {
          const roleResponse = await getAllRole(
            `assigned=false&${office}=${description}`
          );
          if (roleResponse) {
            if (roleResponse.data) {
              roleResponse.data.map((x) => {
                if (!existing.includes(x._id)) {
                  data.push(x);
                  existing.push(x._id);
                }
              });
            }
          }
        } catch (e) {
          console.log(e);
        }
      }
      if (
        vacancy === "" ||
        vacancy === "FuturePromotion" ||
        vacancy === "FutureTerminate" ||
        vacancy === "FutureTransfer"
      ) {
        let que = `${office}=${description}`;
        if (vacancy === "FuturePromotion") {
          que = `${que}&movementType=P`;
        }
        if (vacancy === "FutureTerminate") {
          que = `${que}&movementType=TE`;
        }
        if (vacancy === "FutureTransfer") {
          que = `${que}&movementType=TR`;
        }
        try {
          const futureJob = await getFutureJob(que);
          if (futureJob) {
            if (futureJob.data) {
              futureJob.data.map((x) => {
                if (!existing.includes(x.roleId._id)) {
                  data.push(x.roleId);
                  existing.push(x.roleId._id);
                }
              });
            }
          }
        } catch (e) {
          console.log(e);
        }
      }
      setRoleList(data);
      // console.log(data,"datas");
    };
    modifyData();
  }, [description]);

  const onChangeOffice = (event) => {
    const office = event.target.value;
    // console.log(name)
    if (office === "" || office.length < 2) {
      setOffice(office);
      setDescription("");
      setListData([]);
      setRoleList(allRoles);
    } else {
      setOffice(office);
    }
  };

  const onChangeReliving = (event) => {
    const office = event.target.value;
    let date = new Date(Date.now());
    if (
      dateDuration(
        `${date.getFullYear()}-${date.getMonth() + 1}-${date.getDate()}`,
        office
      ) < 0
    ) {
      toast.warning("Dont select past dates.");
      setRelieving("");
      return 0;
    }
    // console.log(name)
    if (office === "" || office.length < 2) {
      setRelievingError("Please enter at least 3 characters");
    } else {
      setRelieving(office);
      setRelievingError(null);
    }
  };

  const onChangeJoining = (event) => {
    if (relieving === "") {
      toast.warning("Please select the relieving date first.");
      setJoining("");
      return 0;
    }
    const office = event.target.value;
    // console.log(name)
    if (dateDuration(relieving, office) < 0) {
      setJoining("");
      toast.warning("Please select valid dates");
      return 0;
    }
    if (office === "" || office.length < 2) {
      setJoiningError("Please enter at least 3 characters");
    } else {
      setJoining(office);
      setJoiningError(null);
    }
  };

  const dateDuration = (dt1, dt2) => {
    let dt3 = dt1.split("-");
    let dt4 = dt2.split("-");
    console.log(
      parseInt(dt3[0]),
      parseInt(dt3[1]),
      parseInt(dt3[2]),
      parseInt(dt4[0]),
      parseInt(dt4[1]),
      parseInt(dt4[2])
    );
    let dt5 = new Date(parseInt(dt3[0]), parseInt(dt3[1]), parseInt(dt3[2]));
    let dt6 = new Date(parseInt(dt4[0]), parseInt(dt4[1]), parseInt(dt4[2]));
    let diff = dt6.getTime() - dt5.getTime();
    return diff;
  };
  // diff_minutes("17-12-2020","27-12-2020");

  const promoteEmployee = async () => {
    console.log(updateDetails, "id");
    if (updateDetails === "" || updateDetails === undefined) {
      if (
        selected === {} ||
        relieving === "" ||
        joining === "" ||
        checked.length === 0
      ) {
        return toast("Please provoid the details.");
      }
      let data = {
        movementType: "P",
        employeeId: selected.assignedTo._id,
        roleId: selected._id,
        dor: relieving,
        doj: joining,
        toRoleId: checked[0],
      };
      console.log(data);
      try {
        const datas = await addPromoteEmployee(data);
        if (datas) {
          if (datas.success) {
            toast.success(datas.message);
            setTimeout(() => {
              props.change();
            }, 3000);
            setSelected({});
            setRelieving("");
            setJoining("");
            setChecked([]);
          }
        }
      } catch (e) {
        toast.error(e);
        console.log(e);
      }
    } else {
      if (
        selected === {} ||
        relieving === "" ||
        joining === "" ||
        checked.length === 0
      ) {
        return toast.warning("Please provoid the details.");
      }
      let data = {
        id: updateDetails,
        dor: relieving,
        doj: joining,
        toRoleId: checked[0],
      };
      console.log(data);
      try {
        const datas = await updateEmployeeMovement(data);
        if (datas) {
          if (datas.success) {
            toast.success(datas.message);
            setTimeout(() => {
              props.change();
            }, 3000);
            setSelected({});
            setRelieving("");
            setJoining("");
            setChecked([]);
          }
        }
      } catch (e) {
        toast.error(e);
        console.log(e);
      }
    }
    setTimeout(() => {
      props.change();
    }, 3000);
  };

  const onChangedescription = (event) => {
    const description = event.target.value;
    console.log(description);
    setDescription(description);
  };

  const onChangeVacancy = (event) => {
    const vacancy = event.target.value;
    console.log(vacancy);
    setvacancy(vacancy);
  };

  useEffect(() => {
    if (office === "typeOfOffice") {
      const getAllTypeOfOffice = async () => {
        try {
          const typeOfOffice = await getOfficeType();
          if (typeOfOffice) {
            if (typeOfOffice.OfficeTypes) {
              setListData(typeOfOffice.OfficeTypes);
            }
          }
        } catch (e) {
          console.log(e);
        }
      };
      getAllTypeOfOffice();
    }
