                        : ""}{" "}
                    </span>
                  </CLabel>
                </CCol>
              </CRow>
              {rolehierarchy.roleName ? (
                <HierarchyChart datasource={rolehierarchy} />
              ) : null}
            </CModalBody>
          </CModal>

          <CRow style={{ padding: "15px" }}>
            <CCol md="10">
              <CButton
                color={"primary"}
                shape={"pill"}
                className={"btn btn-btn-primary"}
                style={{ position: "relative", right: "-8px" }}
                onClick={() => {
                  setView(false);
                }}
              >
                + ADD
              </CButton>
            </CCol>
            <CCol md="2" style={{ paddingLeft: "10px", marginBottom: "20px" }}>
              <CRow>
                <CCol style={{ paddingTop: "15px" }}>
                  <img
                    src={
                      "https://img.icons8.com/fluent/2x/organization-chart-people.png"
                    }
                    style={{
                      height: "40px",
                      width: "40px",
                      marginLeft: "62px",
                      marginBottom: "-100px",
                    }}
                    onClick={() => setPrimary(!primary)}
                  />
                </CCol>
              </CRow>
            </CCol>
          </CRow>
          <CRow style={{ padding: "3%", paddingLeft: "2%" }}>
            <CCol>
              <CDataTable
                size={"1500px"}
                items={roleListPaginate}
                fields={fields}
                columnFilter
                tableFilter
                footer
                itemsPerPageSelect
                itemsPerPage={5}
                hover
                sorter
                pagination
                scopedSlots={{
                  status: (item) => (
                    <td>
                      <CBadge color={getBadge(item.status)}>
                        {item.status}
                      </CBadge>
                    </td>
                  ),
                  show_details: (item, index) => {
                    return (
                      <td className="py-2">
                        <CRow>
                          <CCol style={{ fontSize: "1.15rem" }} md="12">
                            <i
                              onClick={() => {
                                toggleDetails(index);
                              }}
                            ></i>
                            <i
                              style={{
                                marginRight: "5px",
                                color: "#3480e2",
                              }}
                              onClick={() =>
                                updatoRole(roleListPaginate[index])
                              }
                              className="fas fa-edit"
                            ></i>
                            <i
                              onClick={() =>
                                deletemodal(roleListPaginate[index]._id)
                              }
                              style={{
                                marginLeft: "5px",
                                color: "#e85654",
                              }}
                              className="fas fa-trash"
                            ></i>
                          </CCol>
                        </CRow>
                      </td>
                    );
                  },
                  details: (item, index) => {},
                }}
              />
            </CCol>
          </CRow>
        </CCard>
      )}
      <ToastContainer autoClose={1500}></ToastContainer>
    </div>
  );
}

export default Reporting;
